mapper.sql:
SELECT          idx
     ,          content
     ,          date_format(reg_dt, '%Y-%m-%d') regDt
FROM            tb_schedule
WHERE           date = #{date}
AND             user_id = #{userId}

mapper.update:
UPDATE  tb_schedule
SET     content = #{content}, mod_dt = now()
where   idx = #{idx}

mapper.select:
select count(*)
from tb_schedule
where user_id = #{userId}
group by date
having date = #{date}
