mapper.insert:
INSERT INTO tb_user(
                        user_id
                    ,   user_pw
                    ,   user_name
                    ,   user_nickname
                    ,   user_email
                    ,   user_phone)
    VALUES          (
                        #{userId}
                    ,   #{userPw}
                    ,   #{userName}
                    ,   #{userNickname}
                    ,   #{userEmail}
                    ,   #{userPhone})

mapper.select:
SELECT  COUNT(*)
FROM    tb_user
WHERE   user_email =  #{userEmail}

SELECT COUNT(*)
FROM   tb_user
WHERE  user_id = #{userId}

SELECT COUNT(*)
FROM   tb_user
WHERE  user_nickname = #{userNickname}
